    @Override
    protected void onCreate(Bundle savedInstanceState) {
        if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.O) {
            ActivityHook.hookOrientation(this);//hook，绕过检查
        }
        super.onCreate(savedInstanceState);
        getWindow().setSoftInputMode(WindowManager.LayoutParams.SOFT_INPUT_ADJUST_PAN);
        setContentView(R.layout.cp_activity_city_list);
        mContext = this;
        initData();
        initView();
        BarUtils.setStatusBarColor(this, ContextCompat.getColor(mContext, statusBarColor));
        if (CityPickerMange.getInstance().isStatusBarColorFFF(statusBarColor)) {//设置状态栏字体颜色 为黑色  默认为白色
            BarUtils.setStatusBarLightMode(this, true);
        }
        toolbar.setBackgroundColor(ContextCompat.getColor(mContext, toolbarBarColor));
        txtTitle.setTextColor(ContextCompat.getColor(mContext, toolbarBarTextColor));
        imgBack.setImageResource(toolbarBarBackDrawable);
    }

    CityListBean currentCityListBean;

    private void initData() {
        if (getIntent() != null) {
            currentCityListBean = (CityListBean) getIntent().getSerializableExtra("CityListBean");
            if(null != currentCityListBean){
                locationIcon = currentCityListBean.getLocationIcon();
            }
            statusBarColor = getIntent().getIntExtra(KEY_STATUSBAR_COLOR, CityPickerMange.getInstance().getStatusBarColor());
            toolbarBarColor = getIntent().getIntExtra(KEY_TOOLBARBAR_COLOR, CityPickerMange.getInstance().getToolbarBarColor());
            toolbarBarTextColor = getIntent().getIntExtra(KEY_TOOLBARBAR_TEXTCOLOR, CityPickerMange.getInstance().getToolbarBarTextColor());
            toolbarBarBackDrawable = getIntent().getIntExtra(KEY_TOOLBARBAR_BACKDRAWABLE, CityPickerMange.getInstance().getToolbarBarBackDrawable());
        }

        mCityAdapter = new CityListAdapter(this, mAllCities);
        mCityAdapter.setOnCityClickListener(new CityListAdapter.OnCityClickListener() {//所有城市数据adapter
            @Override
            public void onCityClick(CityListBean cityListBean) {
                back(cityListBean);
            }

            @Override
            public void onLocateClick() {
                mCityAdapter.updateLocateState(LocateState.LOCATING, null);
            }
        });
        getCityData();
        mResultAdapter = new ResultListAdapter(this, null);//搜索结果adapter
    }

    private void initView() {
        toolbar = findViewById(R.id.wcsp_toolbar);
        imgBack = findViewById(R.id.wcsp_img_back);
        txtTitle = findViewById(R.id.wcsp_txt_title);

        mListView = (ListView) findViewById(R.id.listview_all_city);//地址列表
        mListView.setAdapter(mCityAdapter);

        findViewById(R.id.ll_current_city).setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View view) {
                back(currentCityListBean);
            }
        });


        mListView.setOnScrollListener(new AbsListView.OnScrollListener() {
            @Override
            public void onScrollStateChanged(AbsListView view, int scrollState) {
                if (scrollState == AbsListView.OnScrollListener.SCROLL_STATE_IDLE) {//滑动停止
                    mLetterBar.setBackgroundColor(ContextCompat.getColor(mContext, R.color.color_00000000));
                } else {
                    //正在滑动 或正在惯性滑动
                    mLetterBar.setBackgroundColor(ContextCompat.getColor(mContext, R.color.color_30000000));
                }
            }

            @Override
            public void onScroll(AbsListView view, int firstVisibleItem, int visibleItemCount, int totalItemCount) {

            }
        });
        TextView overlay = (TextView) findViewById(R.id.tv_letter_overlay);
        mLetterBar = (SideLetterBar) findViewById(R.id.side_letter_bar);//字母列表
        tvCurrentCity = (TextView) findViewById(R.id.tv_current_city);//显示当前城市
        if(locationIcon!=0){
            try {
                Drawable drawable = getResources().getDrawable(locationIcon, null);
                if(drawable != null) tvCurrentCity.setCompoundDrawablesWithIntrinsicBounds(drawable, null, null, null);
            } catch (Exception e){
                LogUtil.e("CityPickerActivity","定位小图标资源不正确");
            }
        }
        mLetterBar.setOverlay(overlay);
        mLetterBar.setOnLetterChangedListener(new SideLetterBar.OnLetterChangedListener() {
            @Override
            public void onLetterChanged(String letter) {
                if (mAllCities.size() == 0) {
                    return;
                }
                int position = mCityAdapter.getLetterPosition(letter);
                mListView.setSelection(position);
            }
        });


        searchBox = (EditText) findViewById(R.id.et_search);
        searchBox.addTextChangedListener(new TextWatcher() {//搜索框
            @Override
            public void beforeTextChanged(CharSequence s, int start, int count, int after) {
            }

            @Override
            public void onTextChanged(CharSequence s, int start, int before, int count) {
            }

            @Override
            public void afterTextChanged(Editable s) {
                String keyword = s.toString();
                if (TextUtils.isEmpty(keyword)) {
                    clearBtn.setVisibility(View.GONE);
                    emptyView.setVisibility(View.GONE);
                    mResultListView.setVisibility(View.GONE);
                } else {
                    clearBtn.setVisibility(View.VISIBLE);
                    mResultListView.setVisibility(View.VISIBLE);
                    List<CityListBean> result = citySearch(keyword);//todo search;
                    if (result == null || result.size() == 0) {
                        emptyView.setVisibility(View.VISIBLE);
                    } else {
                        emptyView.setVisibility(View.GONE);
                        mResultAdapter.changeData(result);
                    }
                }
            }
        });

        emptyView = (LinearLayout) findViewById(R.id.empty_view);
        mResultListView = (ListView) findViewById(R.id.listview_search_result);//搜索结果列表
        mResultListView.setAdapter(mResultAdapter);
        mResultListView.setOnItemClickListener(new AdapterView.OnItemClickListener() {
            @Override
            public void onItemClick(AdapterView<?> parent, View view, int position, long id) {
                back(mResultAdapter.getItem(position));
            }
        });

        clearBtn = (ImageView) findViewById(R.id.iv_search_clear);
        clearBtn.setOnClickListener(this);
        imgBack.setOnClickListener(this);
        initCurrentLocationView();
    }

    private void initCurrentLocationView() {
        if (null != currentCityListBean && !TextUtils.isEmpty(currentCityListBean.getName())) {
            tvCurrentCity.setText(currentCityListBean.getName());
        }
    }

    public List<CityListBean> citySearch(String keyWord) {//城市搜索
        List<CityListBean> result = new ArrayList<>();
        try {
            Pattern pattern = Pattern.compile(keyWord, Pattern.CASE_INSENSITIVE);

            if (pattern == null) return result;

            for (CityListBean cityListBean : mAllCities) {
                Matcher matcher = pattern.matcher((cityListBean.getPinyin()));
                Matcher matcherName = pattern.matcher((cityListBean.getName()));
                if (matcher.find() || matcherName.find()) {
                    result.add(cityListBean);
                }
            }
            return result;
        } catch (Exception e) {
            return result;
        }
    }

    private void back(CityListBean cityListBean) {
        if (null != cityListBean) {
            Intent data = new Intent();
            data.putExtra(KEY_PICKED_CITY, cityListBean);
            setResult(RESULT_OK, data);
            finish();
        }
    }

    @Override
    public void onClick(View v) {
        int i = v.getId();
        if (!Utils.enableClick()) return;
        if (i == R.id.iv_search_clear) {
            searchBox.setText("");
            clearBtn.setVisibility(View.GONE);
            emptyView.setVisibility(View.GONE);
            mResultListView.setVisibility(View.GONE);
        } else if (i == R.id.wcsp_img_back) {
            finish();
        }
    }

    @Override
    protected void onPause() {
        super.onPause();
        InputMethodManager imm = (InputMethodManager) getSystemService(Context.INPUT_METHOD_SERVICE);//隐藏软键盘
        imm.hideSoftInputFromWindow(getWindow().getDecorView().getWindowToken(), 0);
    }

    @Override
    protected void onDestroy() {
        super.onDestroy();
    }

