    public <T> List<T> getDataListBean(String data, Class<T> clazz) {
        try {
            if (data != null) {
                Type type = new TypeToken<ArrayList<JsonObject>>() {
                }.getType();
                ArrayList<JsonObject> jsonObjects = new Gson().fromJson(data, type);
                ArrayList<T> arrayList = new ArrayList<>();
                if (jsonObjects == null || jsonObjects.size() == 0) {
                    return arrayList;
                }
                for (JsonObject jsonObject : jsonObjects) {
                    arrayList.add(new Gson().fromJson(jsonObject, clazz));
                }
                return arrayList;
            }
        } catch (Exception e) {
            e.printStackTrace();
        }
        return null;
    }

    public void addMsg(List<CityPicker> cityList) {
        for (CityPicker cityPicker : cityList) {
            if (cityPicker.getCityList() == null || cityPicker.getCityList().size() == 0) break;
            for (CityListBean cityListBean : cityPicker.getCityList()) {
                if (!TextUtils.isEmpty(cityListBean.getName()) && !TextUtils.isEmpty(cityListBean.getPinyin())) {
                    mAllCities.add(cityListBean);
                }
            }
        }
        mCityAdapter.setData(mAllCities);
        mCityAdapter.notifyDataSetChanged();
    }


